package com.lonfe;import com.lonfe.service.HelloService;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;public class Client {	public static void main(String[] args) {		ApplicationContext context = new ClassPathXmlApplicationContext("context.xml");		HelloService service = (HelloService) context.getBean("hello");		String name = service.say("LF");		System.out.println("name: " + name);	}}